package com.la.night_owl.Calculator_demo;


public class Calculator_ArthmeticOper_Library {
	
	private Calculator_ArthmeticOper_Library() {
		// TODO Auto-generated constructor stub
	}
	public static int add(int n1, int n2) { return n1+n2; }
	public static int sub(int n1, int n2) { return n1-n2; }
	public static int mul(int n1, int n2) { return n1*n2; }
	public static int div(int n1, int n2) { return n1/n2; }
	public static int mod(int n1, int n2) { return n1%n2; }

}
